@startuml
abstract class User {
    - id : int
    - name : string
    + {abstract} getInfo()
    + {abstract} addRole(spec)
    + {abstract} getRole(spec)
}

abstract class UserRole extends User {
    + getInfo()
    + addRole(spec)
    + getRole(spec)
}

class UserCore extends User {
    + getInfo()
    + addRole(spec)
    + getRole(spec)
}

class Operator extends UserRole {
    + monitorSignal()
}

class Engineer extends UserRole {
    + performMaintenance()
}

class Analyst extends UserRole {
    + analyzeTrafficData()
}

class TrafficController {
    - static instance : TrafficController
    - signals : List<string>
    + getInstance() : TrafficController
    + manageSignal(location : string, status : string)
}

UserRole --> UserCore : core
UserCore o-- UserRole : role
@enduml

