    + createStorage() : Storage
    + createUI() : UI
}

class WebPlayer extends Player {
    + play(song : string)
}

class MobilePlayer extends Player {
    + play(song : string)
}

class DesktopPlayer extends Player {
    + play(song : string)
}

class WebStorage extends Storage {
    + saveData(data : string)
}

class MobileStorage extends Storage {
    + saveData(data : string)
}

class DesktopStorage extends Storage {
    + saveData(data : string)
}

class WebUI extends UI {
    + render()
}

class MobileUI extends UI {
    + render()
}

