@startuml
abstract class FileSystemComponent {
    + {abstract} getName() : string
    + {abstract} getSize() : int
    + {abstract} display(indent : int)
    + {abstract} add(FileSystemComponent)
    + {abstract} remove(FileSystemComponent)
}

class FileLeaf extends FileSystemComponent {
    - name : string
    - size : int
    + getName() : string
    + getSize() : int
    + display(indent : int)
}

class FolderComposite extends FileSystemComponent {
    - name : string
    - components : List<FileSystemComponent>
    + add(c : FileSystemComponent)
    + remove(c : FileSystemComponent)
    + getName() : string
    + getSize() : int
    + display(indent : int)
}

FolderComposite o-- FileSystemComponent
@enduml

