    + manageRecords()
}

abstract class StaffFactory {
    + {abstract} createStaff(type : string) : Staff
}

class HospitalStaffFactory extends StaffFactory {
    + createStaff(type : string) : Staff
}

StaffRole --> StaffCore : core
StaffCore o-- StaffRole : role
StaffFactory o-- Staff
@enduml

